                DECODE(CMOP.COMPDICI||DEPA.COMPDICI,'SS','12',DECODE(CMOP.COMPNOVI||DEPA.COMPNOVI,'SS','11',DECODE(CMOP.COMPOCTU||DEPA.COMPOCTU,'SS','10',DECODE(CMOP.COMPSEPT||DEPA.COMPSEPT,'SS','09',DECODE(CMOP.COMPAGOS||DEPA.COMPAGOS,'SS','08',DECODE(CMOP.COMPJULI||DEPA.COMPJULI,'SS','07',
