                DECODE(CMOP.COMPDICI,'S','12',DECODE(CMOP.COMPNOVI,'S','11',DECODE(CMOP.COMPOCTU,'S','10',DECODE(CMOP.COMPSEPT,'S','09',DECODE(CMOP.COMPAGOS,'S','08',DECODE(CMOP.COMPJULI,'S','07',
