                DECODE(DEPA.COMPDICI,'S','12',DECODE(DEPA.COMPNOVI,'S','11',DECODE(DEPA.COMPOCTU,'S','10',DECODE(DEPA.COMPSEPT,'S','09',DECODE(DEPA.COMPAGOS,'S','08',DECODE(DEPA.COMPJULI,'S','07',
                DECODE(DEPA.COMPJUNI,'S','06',DECODE(DEPA.COMPMAYO,'S','05',DECODE(DEPA.COMPABRI,'S','04',DECODE(DEPA.COMPMARZ,'S','03',DECODE(DEPA.COMPFEBR,'S','02',DECODE(DEPA.COMPENER,'S','01')))))))))))) ULTIMO_MES_CARGADO_FINANCIERO,
                CMOP.COMPENER MES_DE_ENER_CARGADO_FINANCIERO, CMOP.COMPFEBR MES_DE_FEBR_CARGADO_FINANCIERO, 
                CMOP.COMPMARZ MES_DE_MARZ_CARGADO_FINANCIERO, CMOP.COMPABRI MES_DE_ABRI_CARGADO_FINANCIERO, 
